
public class 문자열_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="Hello ";
		String s2="Java!";
		//문자열 결합
		System.out.println(s1+s2);
		System.out.println(s1.concat(s2)); // s1뒤에 s2를 붙이라는 명령어 .concat()
		// 문자열 ||, concat(s1,s2) => 오라클
		// 5/2 = 2
	}

}
